package com.creatio.imm;

import android.content.Context;
import android.content.Intent;
import android.content.SharedPreferences;
import android.graphics.Paint;
import android.preference.PreferenceManager;
import android.support.annotation.Nullable;
import android.support.v7.app.AppCompatActivity;
import android.os.Bundle;
import android.util.Log;
import android.view.View;
import android.widget.Button;
import android.widget.TextView;
import android.widget.Toast;

import com.android.volley.AuthFailureError;
import com.android.volley.Request;
import com.android.volley.RequestQueue;
import com.android.volley.Response;
import com.android.volley.VolleyError;
import com.android.volley.toolbox.StringRequest;
import com.android.volley.toolbox.Volley;
import com.google.zxing.integration.android.IntentIntegrator;
import com.google.zxing.integration.android.IntentResult;
import com.tapadoo.alerter.Alerter;

import org.json.JSONException;
import org.json.JSONObject;

import java.util.HashMap;
import java.util.Map;

public class TarjetaUnidos extends AppCompatActivity {

    private Context context;
    private Button btnCamaraBar;
    private TextView textpara;
    private Button btnContinuar1;
    private String numTarjeta;
    private String ids;
    private String code;
    private String name;
    private String last_name;
    private String middle_name;
    private String photo;
    private SharedPreferences prefs;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        context = TarjetaUnidos.this;
        prefs = PreferenceManager.getDefaultSharedPreferences(context);
        setContentView(R.layout.activity_tarjeta__unidos);
        TextView textAyuda = (TextView) findViewById(R.id.textAyuda);
        textAyuda.setPaintFlags(textAyuda.getPaintFlags() | Paint.UNDERLINE_TEXT_FLAG);
        btnCamaraBar = findViewById(R.id.btnCamaraBar);
        textpara = findViewById(R.id.textpara);
        btnContinuar1 = findViewById(R.id.btnContinuar1);

        btnCamaraBar.setOnClickListener(mOnclickListener);
        btnContinuar1.setOnClickListener(siguienteA);
    }

    @Override
    protected void onActivityResult(int requestCode, int resultCode, @Nullable Intent data) {
        super.onActivityResult(requestCode, resultCode, data);
        IntentResult result = IntentIntegrator.parseActivityResult(requestCode,resultCode,data);
            if ( result != null && result.getContents() != null  ){
                numTarjeta = result.getContents();
                btnCamaraBar.setText(numTarjeta);
                Toast.makeText(this,"El codigo de la tarjeta es:\n"+ numTarjeta, Toast.LENGTH_SHORT);
            }else {
                Alerter.create(TarjetaUnidos.this)
                        .setText("No existe informaci칩n scaneada.")
                        .setBackgroundColorInt(getResources().getColor(R.color.red))
                        .show();
            }
    }

    private View.OnClickListener mOnclickListener = new View.OnClickListener() {
        @Override
        public void onClick(View v) {
            switch (v.getId()){
                case R.id.btnCamaraBar:
                    IntentIntegrator integrator = new IntentIntegrator(TarjetaUnidos.this);
                    integrator.setPrompt("Coloque su tarjeta en el interior del rect치ngulo del visor para escanear.");
                    integrator.initiateScan();
                    break;
            }
        }
    };

    private View.OnClickListener siguienteA = new View.OnClickListener() {
        @Override
        public void onClick(View v) {
            btnCamaraBar.setText(numTarjeta);
            numTarjeta = "987654321";
            if (numTarjeta != null && numTarjeta.length() > 0 ){
                StringRequest stringRequest = new StringRequest(Request.Method.POST, getResources().getString(R.string.apitarurl), new Response.Listener<String>() {
                    @Override
                    public void onResponse(String response) {
                        try {
                            JSONObject json = new JSONObject(response);
                            Log.e("response", response);
                            String success = json.getString("success");
                            if (success.equalsIgnoreCase("true")) {
                                String datos = json.getString("card");
                                Log.e("user", datos);
                                JSONObject card = new JSONObject(datos);
                                ids = card.optString("ID");
                                code = card.optString("code");
                                name = card.getString("name");
                                last_name = card.getString("last_name");
                                middle_name = card.getString("middle_name");
                                photo = card.getString("photo");
                                Log.e("photo",photo);
                                if (photo == null || photo == "null" || photo== "") {
                                    Log.e("photo1",photo);
                                    String base64String = "data:image/png;base64,";
                                    String base64Image = base64String.split(",")[1];
                                    photo = base64Image;
                                    Log.e("photo2",photo);
                                }
                                Log.e("photo3",photo);
                               if (numTarjeta.equals(code)){
                                   final Intent i = new Intent(context,EditPerfilTarjeta.class);
                                   i.putExtra("ids",ids);
                                   i.putExtra("numTarjeta",code );
                                   i.putExtra("name",name );
                                   i.putExtra("last_name",last_name );
                                   i.putExtra("middle_name",middle_name );
                                   i.putExtra("photo",photo );
                                   //finish();
                                   startActivity(i);
                               }
                            } else {
                                Alerter.create(TarjetaUnidos.this)
                                        .setTitle("Oh oh")
                                        .setText("No existe informaci칩n con los datos proporcionados..")
                                        .setBackgroundColorInt(getResources().getColor(R.color.red))
                                        .show();
                            }
                        } catch (JSONException e) {
                            Alerter.create(TarjetaUnidos.this)
                                    .setTitle("Oh oh")
                                    .setText("No existe informaci칩n con los datos proporcionados...")
                                    .setBackgroundColorInt(getResources().getColor(R.color.red))
                                    .show();
                            e.printStackTrace();
                        }
                    }
                }, new Response.ErrorListener() {
                    @Override
                    public void onErrorResponse(VolleyError error) {
                        // Anything you want
                    }
                }) {
                    @Override
                    public Map<String, String> getHeaders() throws AuthFailureError {
                        Map<String, String> params = new HashMap<String, String>();
                        params.put("Content-Type", "application/x-www-form-urlencoded");
                        return params;
                    }

                    @Override
                    protected Map<String, String> getParams() {
                        Map<String, String> params = new HashMap<String, String>();
                        params.put("apikey", getResources().getString(R.string.apikey));
                        params.put("id", numTarjeta);
                        return params;
                    }
                };
                RequestQueue requestQueue = Volley.newRequestQueue(getApplicationContext());
                requestQueue.add(stringRequest);
            }else {
                Alerter.create(TarjetaUnidos.this)
                        .setText("Favor de escanear un codigo de tarjeta\n")
                        .setBackgroundColorInt(getResources().getColor(R.color.red))
                        .show();
            }
        }
    };
}
